import smartpy as sp

# Builds an SVG by concatenating different sections of the data URI
# Final sample output given at ./samples/svg_example.svg
def build_svg(params):
    sp.set_type(
        params,
        sp.TRecord(token_id=sp.TBytes, amount=sp.TBytes, value=sp.TBytes),
    )

    return (
        sp.utils.bytes_of_string(DATA_1)
        + params.amount
        + sp.utils.bytes_of_string(DATA_2)
        + params.value
        + sp.utils.bytes_of_string(DATA_3)
        + params.token_id
        + sp.utils.bytes_of_string(DATA_4)
        + params.token_id
        + sp.utils.bytes_of_string(DATA_5)
    )


